
#include <stdio.h>
#include <string.h>

static const char * burst = "----------------------------------------";

int main()
{
	int i;
	unsigned char symbol;

	while ( 1 )
	{
		for ( i = 0; i < strlen(burst); i += 2 )
		{
			if ( burst[i] == '0' && burst[i+1] == '0' )
				symbol = 0;
			else if ( burst[i] == '0' && burst[i+1] == '1' )
				symbol = 1;
			else if ( burst[i] == '1' && burst[i+1] == '0' )
				symbol = 2;
			else if ( burst[i] == '1' && burst[i+1] == '1' )
				symbol = 3;
			else
				symbol = 0xff;

			printf("%c", symbol);
		}
	}

	return 0;
}
